export const EventDispatcher = () => {
  return (object: any, propertyName: string, index?: number): void => {
    const eventDispatcher = new EventDispatcherClass();
    Container.registerHandler({
      object,
      propertyName,
      index,
      value: () => eventDispatcher,
    });
  };
};
