import createError from 'http-errors';
import type { ErrorRequestHandler } from 'express';
import httpStatus from 'http-status-codes';

import { logger, loggerDev } from '../utils/logger';
import { ErrorRo } from '../types/appRo';

/**
 * Error middleware
 * Every error thrown in a route ends up here to be sent to the user
 * They are formatted into a generic RO, to have uniform error replies
 *
 * Not wanted errors (for example, a crash in the route) are
 * converted into a 500 - Internal server errors
 */
const errorRequest: ErrorRequestHandler = (err, req, res, _) => {
  logger.error(err.message);
  // If the error is not an HTTP error, the whole object is printed through console.error
  if (!createError.isHttpError(err)) {
    loggerDev.error(err);
  }
  const status = err.status ?? httpStatus.INTERNAL_SERVER_ERROR;
  res.status(status).send(ErrorRo(status, err.message));
};

export { errorRequest };

export default errorRequest;

/**
 * Basic interface representing a Response Object (RO), from which all ROs inherits
 * It may contains an error and enable API consumer to treat every responses the same way
 */
export interface Ro {
  error?: {
    statusCode: number;
    message: string;
  };
}

/**
 * Construct a basic error RO
 *
 * @param statusCode HTTP status code
 * @param message Error message
 * @constructor
 */
export function ErrorRo(statusCode: number, message: string): Ro {
  return {
    error: {
      statusCode,
      message,
    },
  };
}

import { plainToClass } from 'class-transformer';
import { validate } from 'class-validator';
import { RequestHandler } from 'express';
import httpStatus from 'http-status-codes';
import createError from 'http-errors';
import handler from 'express-async-handler';
import { ClassType } from 'class-transformer/ClassTransformer';

/**
 * Validate the request body
 * Data Transfer Object: DTO
 * @param type The DTO object, defining the shape of the body
 * @throws 400 - Bad request | If at least one constraint is not respected
 *
 * @example
 * import validate from 'middlewares/validationMiddleware'
 *
 * class UserSignupDto {
 *  @IsEmail()
 *  email!: string;
 *
 *  @IsString()
 *  @IsOptional()
 *  name!: string;
 *
 *  @IsString()
 *  @MinLength(8)
 *  @MaxLength(64)
 *  password!: string;
 * }
 * router.post('/users/signup', validate(UserSignupDto), ...);
 */
function validation<T>(type: ClassType<T>): RequestHandler {
  return handler(async (req, res, next) => {
    const parsedBody = plainToClass(type, req.body);
    const errors = await validate(parsedBody);
    if (errors.length !== 0) {
      const message = errors.join('').trimEnd();
      next(createError(httpStatus.BAD_REQUEST, message));
    } else {
      req.body = parsedBody;
      next();
    }
  });
}

